// Extract Telegram message and normalize into { query, chatId }
const msg = $json.message || {};
const text = (msg.text || '').trim();
const chatId = msg.chat?.id;

if (!text) {
  throw new Error('No text message received from Telegram.');
}

return [{
  json: {
    query: text,   // question from user
    chatId        // for reply
  }
}];
